package CoreJava;

public class Loopsconcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//while loop
		
		//disadvantage of while loop if you dont give incremental/decremental
		
		int i = 1;
		
		while (i<=10) {
			System.out.println(i);
			i++;
			
		           }
		
		//for loop
		
				
		for(int j = 10;j>=1;j--){ // initialization,conditioning,incremental or decremental
			
			System.out.println(j);
			
		}

	}

}
